-- public.bal_prdords source
drop view if exists bal_prdords;
CREATE OR REPLACE VIEW public.bal_prdords
AS SELECT chrg.person_name_chrg,
    chrg.person_code_chrg,
    opeitm.itm_name,
    opeitm.itm_code,
    opeitm.unit_name,
    opeitm.unit_code,
    opeitm.itm_unit_id,
    opeitm.opeitm_processseq,
    opeitm.opeitm_packqty,
    opeitm.opeitm_priority,
    opeitm.opeitm_itm_id,
    opeitm.opeitm_duration,
    prdord.id,
    prjno.prjno_name,
    chrg.person_sect_id_chrg,
    person_upd.code AS person_code_upd,
    person_upd.name AS person_name_upd,
    prdord.expiredate AS prdord_expiredate,
    prdord.updated_at AS prdord_updated_at,
    prdord.qty AS prdord_qty,
	func_get_purprd_qty_bal('prdords',prdord.id) linktbl_qty_bal,
    prdord.sno AS prdord_sno,
    prdord.remark AS prdord_remark,
    prdord.created_at AS prdord_created_at,
    prdord.update_ip AS prdord_update_ip,
    prdord.duedate AS prdord_duedate,
    prdord.toduedate AS prdord_toduedate,
    prdord.id AS prdord_id,
    prdord.persons_id_upd AS prdord_person_id_upd,
    prdord.isudate AS prdord_isudate,
    shelfno.shelfno_code,
    shelfno.shelfno_name,
    shelfno.shelfno_loca_id_shelfno,
    shelfno.loca_code_shelfno,
    shelfno.loca_name_shelfno,
    prjno.prjno_code,
    chrg.chrg_person_id_chrg,
    opeitm.classlist_code,
    opeitm.classlist_name,
    prdord.prjnos_id AS prdord_prjno_id,
    prdord.gno AS prdord_gno,
    opeitm.boxe_unit_id_box,
    opeitm.boxe_code,
    opeitm.boxe_name,
    opeitm.opeitm_boxe_id,
    prdord.opeitms_id AS prdord_opeitm_id,
    prdord.chrgs_id AS prdord_chrg_id,
    prdord.starttime AS prdord_starttime,
    prjno.prjno_code_chil,
    opeitm.opeitm_unitofduration,
    prdord.confirm AS prdord_confirm,
    opeitm.itm_classlist_id,
    shelfno_to.shelfno_code AS shelfno_code_to,
    shelfno_to.shelfno_name AS shelfno_name_to,
    shelfno_to.loca_code_shelfno AS loca_code_shelfno_to,
    shelfno_to.loca_name_shelfno AS loca_name_shelfno_to,
    shelfno_to.shelfno_loca_id_shelfno AS shelfno_loca_id_shelfno_to,
    prdord.shelfnos_id_to AS prdord_shelfno_id_to,
    prjno.prjno_name_chil,
    opeitm.shelfno_code_to_opeitm,
    opeitm.shelfno_name_to_opeitm,
    opeitm.loca_code_shelfno_to_opeitm,
    opeitm.loca_name_shelfno_to_opeitm,
    opeitm.unit_name_case_shp,
    opeitm.unit_code_case_shp,
    opeitm.unit_name_case_prdpur,
    opeitm.unit_code_case_prdpur,
    opeitm.opeitm_shelfno_id_opeitm,
    opeitm.shelfno_code_opeitm,
    opeitm.shelfno_name_opeitm,
    opeitm.shelfno_loca_id_shelfno_opeitm,
    opeitm.loca_code_shelfno_opeitm,
    opeitm.loca_name_shelfno_opeitm,
    prdord.shelfnos_id AS prdord_shelfno_id,
    opeitm.opeitm_shpordauto,
    opeitm.opeitm_prdpurordauto,
    opeitm.opeitm_itmtype,
    prdord.cmplflg AS prdord_cmplflg,
    opeitm.itm_taxflg,
    opeitm.opeitm_utilizationchangeover
   FROM prdords prdord,
    persons person_upd,
    r_prjnos prjno,
    r_opeitms opeitm,
    r_chrgs chrg,
    r_shelfnos shelfno_to,
    r_shelfnos shelfno
  WHERE prdord.persons_id_upd = person_upd.id AND prdord.prjnos_id = prjno.id AND prdord.opeitms_id = opeitm.id AND prdord.chrgs_id = chrg.id AND prdord.shelfnos_id_to = shelfno_to.id AND prdord.shelfnos_id = shelfno.id;


-- sio.sio_r_prdords definition

-- Drop table

DROP TABLE if exists sio.sio_bal_prdords;

